package com.bridgelabz.generic;

public class Generic_PrintArray {

	
		// TODO Auto-generated method stub
public static void toPrint(Integer[] inputArray) {
	for(int element: inputArray) { System.out.printf("%s", element); }

	System.out.println();

	}

	public static void toPrint (Double[] inputArray) { for(double element: inputArray) { System.out.printf("%s", element); }

	System.out.println();

	}

	public static void toPrint (Character [] inputArray) { for(char element: inputArray) { System.out.printf("%s", element);

	System.out.println();

	}

	
	}}
